INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(235, 1, '2021-09-24 04:09:03', '2021-09-24 04:09:03', '<!-- wp:paragraph -->\n<p>&nbsp;Sáng ngày 20/11/2016, tại Hội trường Thư viện, Trường Đại học Đà Lạt đã long trọng tổ chức Lễ kỷ niệm 34 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2016) và trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tham dự buổi Lễ, Trường Đại học Đà Lạt rất vinh dự được đón tiếp:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em><strong>• Về khách mời:</strong></em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Trương Mỹ Hoa, nguyên Bí thư Trung ương Đảng, nguyên Phó Chủ tịch nước Cộng hòa xã hội chủ nghĩa Việt Nam.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Nguyễn Thị Minh, nguyên ủy viên Ban Chấp hành Trung ương Đảng, nguyên Bí thư Tỉnh ủy Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Lê Thị Bân, nguyên ủy viên Ban Chấp hành Trung ương Đảng, nguyên Bí thư Tỉnh ủy Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Huỳnh Đức Hòa, nguyên Bí thư Tỉnh ủy, nguyên Chủ tịch Hội đồng nhân dân tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Nguyễn Thị Thu Thủy, nguyên Chủ tịch UBND tỉnh Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Trung tướng Triệu Xuân Hòa, nguyên Phó Trưởng ban chỉ đạo Tây Nguyên.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Nguyễn Thị Hiếu, Bí thư Huyện ủy Tân Châu, tỉnh Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Thiếu tướng Hoàng Văn Minh, Giám đốc Học viện lục quân Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Võ Thị Hảo, Giám đốc Sở khoa học và Công nghệ Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đại tá Nguyễn Quang Thống, Phó Giám đốc Công an tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ông Phan Minh Thành, nguyên Trưởng ban khu kinh tế Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ông Yoo Tae Huyn, Cựu Đại sứ Đại Hàn Dân Quốc tại Việt Nam; Giáo sư Danh dự Trường Đại học Đà Lạt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – TS. Đặng Quốc Chính, Chủ tịch HĐQT Công ty Cổ phần Du lịch Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;– Ông Johnathan Hạnh Nguyễn, Chủ tịch Hội đồng quản trị Công ty TNHH Xuất Nhập khẩu Liên Thái Bình Dương, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Lê Hồng Thủy Tiên, Tổng Giám đốc Công ty TNHH Xuất Nhập khẩu Liên Thái Bình Dương, phu nhân ông Johnathan Hạnh Nguyễn.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em><strong>• Về phía nhà trường</strong></em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – PGS.TS. Nguyễn Đức Hòa, Bí thư Đảng ủy, Hiệu trưởng Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Các ông bà trong Ban Chấp hành Đảng bộ; Ban Giám hiệu; Hội đồng trường; Ban Chấp hành Công đoàn; Đoàn Thanh niên; Hội Sinh viên; Hội Cựu Chiến binh; các đồng chí lãnh đạo, nguyên lãnh đạo các phòng chức năng, khoa chuyên môn, viện nghiên cứu và các đơn vị trực thuộc; các cán bộ viên chức và 250 sinh viên, học viên cao học, nghiên cứu sinh đại diện cho hơn 13.000 sinh viên nhà trường.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A2471.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>PGS. TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng đọc diễn văn kỷ niệm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Về dự lễ hội truyền thống còn có các vị đại biểu đại diện lãnh đạo các sở, ban, ngành của tỉnh Lâm Đồng và thành phố Đà Lạt; các cơ quan Trung ương trên địa bàn tỉnh Lâm Đồng và các tỉnh trong khu vực Tây Nguyên, Nam Trung bộ và Đông Nam bộ; các tổ chức quốc tế, các giáo sư, tiến sĩ trong nước và nước ngoài đã tham gia giảng dạy, hợp tác với Trường Đại học Đà Lạt; các cơ quan truyền thông của Trung ương, thành phố Hồ Chí Minh và tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A2497.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>PGS. TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng&nbsp;trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn, cựu sinh viên ngành Quản trị kinh doanh, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt, Chủ tịch Hội đồng quản trị Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9091.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Đồng chí Trương Mỹ Hoa – Nguyên Bí thư Trung ương Đảng, nguyên Phó Chủ tịch nước Cộng hòa xã hội chủ nghĩa Việt Nam tặng hoa và phát biểu chúc mừng&nbsp;ông Johnathan Hạnh Nguyễn</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9169.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Ông Johnathan Hạnh Nguyễn – Tiến sĩ danh dự,&nbsp;cựu sinh viên ngành Quản trị kinh doanh, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt, Chủ tịch Hội đồng quản trị Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC phát biểu tại buổi lễ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong niềm vui phấn khởi, PGS.TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng thay mặt lãnh đạo nhà trường đã đọc diễn văn kỷ niệm 34 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2016) ôn lại truyền thống “Tôn sư trọng đạo” của dân tộc ta, ghi nhận những đóng góp to lớn của cán bộ và sinh viên các thế hệ nhà trường. Trong ngày lễ kỷ niệm long trọng hôm nay, Trường Đại học Đà Lạt quyết định trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn, cựu sinh viên ngành Quản trị kinh doanh, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt, Chủ tịch Hội đồng quản trị Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC đã có những cống hiến, đóng góp to lớn cho đất nước và ngành giáo dục nói chung, Trường Đại học Đà Lạt nói riêng. Ông Johnathan Hạnh Nguyễn đã được Nhà nước tặng thưởng Huân chương Lao Động hạng Ba năm 2009, hạng Nhì năm 2011, hạng Nhất năm 2015; Huân chương Hữu nghị về những đóng góp tích cực trong xây dựng củng cố và phát triển mối quan hệ hữu nghị hợp tác giữa Việt Nam và Phillipines, cùng nhiều phần thưởng cao quý khác. Ông đã tích cực đóng góp cho các hoạt động xã hội từ thiện hướng về cộng đồng. Đặc biệt là hỗ trợ phát triển ngành giáo dục như: Tài trợ xây dựng trường học tại huyện đảo Trường Sa; đóng góp hơn 6 triệu USD vào các quỹ từ thiện xã hội; trao tặng hơn 40.000 suất học bổng cho trẻ em nghèo của 63 tỉnh thành không có điều kiện đến trường; cung cấp thiết bị phục vụ dạy và học tại các tỉnh vùng sâu, vùng biên giới, hải đảo; hiện nay Ông còn là Phó chủ nhiệm câu lạc bộ “Vì Hoàng Sa, Trường Sa thân yêu”.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Xúc động, vinh dự được nhận bằng Tiến sĩ danh dự đầu tiên của Trường Đại học Đà Lạt, ông Johnathan Hạnh Nguyễn gửi lời cảm ơn tới Đảng ủy, Ban Giám hiệu, Hội đồng trường, toàn thể cán bộ và sinh viên nhà trường. Ông cũng mong muốn sẽ tiếp tục đồng hành cùng tập thể lãnh đạo và cán bộ nhà trường để hoạch định chiến lược xây dựng Trường Đại học Đà Lạt không ngừng phát triển, thành trường đại học trọng điểm quốc gia và là điểm sáng của giáo dục nước nhà nói chung và Tây Nguyên nói riêng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ghi nhận những cống hiến của cán bộ và sinh viên trường Đại học Đà Lạt, trong năm học 2015 – 2016, Đảng, Nhà nước, Bộ Giáo dục và Đào tạo đã tặng nhiều phần thưởng cao quý cho tập thể và cá nhân của trường: 02 nhà giáo được Hội đồng Chức danh giáo sư Nhà nước công nhận đạt chuẩn chức danh Phó Giáo sư; 10 nhà giáo được Bộ Nội vụ bổ nhiệm chức danh giảng viên cao cấp; nhà trường được Bộ Giáo dục và Đào tạo tặng thưởng Cờ thi đua về thành tích xuất sắc trong công tác đào tạo nguồn nhân lực cho Tây Nguyên, duyên hải Nam Trung bộ và Đông Nam bộ; 08 đơn vị được công nhận là tập thể lao động xuất sắc; 44 cán bộ viên chức được tặng Bằng khen của Bộ GD&amp;ĐT; 10 cán bộ được tặng Kỷ niệm chương “Vì sự nghiệp giáo dục” trong đó có 05 cán bộ thuộc Công an các cấp tỉnh Lâm Đồng và 01 cán bộ lãnh đạo Sở Khoa học và Công nghệ Lâm Đồng đã có những đóng góp quan trọng trong công tác giữ vững an ninh chính trị, trật tự an toàn xã hội và nghiên cứu khoa học, hợp tác quốc tế, góp phần vào sự nghiệp xây dựng, phát triển nhà trường. Đặc biệt, Trường Đại học Đà Lạt đã được Chính phủ khẳng định đầu tư xây dựng thành trường đại học trọng điểm quốc gia vùng Tây Nguyên.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9205.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;Hiệu trưởng Nguyễn Đức Hòa gửi thư và hoa chúc mừng 06 cán bộ giảng viên của trường bảo vệ thành công luận án Tiến sĩ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9215.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Hiệu trưởng Nguyễn Đức Hòa và ông Huỳnh Đức Hòa trao Kỷ niệm chương&nbsp;“Vì sự nghiệp giáo dục” của Bộ trưởng Bộ Giáo dục cho các cán bộ trong và ngoài ngành Giáo dục</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9239.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Chủ tịch Hội đồng trường Cao Thế Trình trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo&nbsp;cho 06 cán bộ của Trường&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9247.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Lê Hồng Phong&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho 03 nguyên cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9259.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Nguyễn Văn Kết&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho các cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9264.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Nguyễn Duy Mậu&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho các cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9270.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Lê Minh Chiến&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho các cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong buổi lễ long trọng này, Hiệu trưởng Nguyễn Đức Hòa gửi thư và hoa chúc mừng 06 cán bộ giảng viên của trường vừa bảo vệ thành công luận án Tiến sĩ, góp phần nâng cao uy tín và vị thế của nhà trường. Nhân dịp này, Trường Đại học Đà Lạt tổ chức trao phần thưởng âm nhạc cho 02 tác giả có sáng tác âm nhạc viết về nhà trường được cán bộ và sinh viên yêu thích:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ca khúc: Bài ca Đại học Đà Lạt – Tác giả: Nguyễn An Ninh, nguyên Hiệu trưởng Trường Đại học Đà Lạt giai đoạn 1990 – 1997.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ca khúc: Giảng đường Tháp sao – Tác giả: Nguyễn Duy Chính, nguyên Giảng viên chính khoa Sinh học, Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đại diện cán bộ được nhận Kỷ niệm chương “Vì sự nghiệp giáo dục”, bà Võ Thị Hảo, Giám đốc Sở khoa học &amp; Công nghệ Lâm Đồng, cựu sinh viên nhà trường đã phát biểu tri ân và mong muốn tiếp tục đóng góp một phần nhỏ bé vào sự nghiệp giáo dục – đào tạo nói chung, nghiên cứu khoa học và chuyển giao công nghệ của trường nói riêng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9278.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Hiệu trưởng Nguyễn Đức Hòa&nbsp;trao phần thưởng âm nhạc cho tác giả có sáng tác âm nhạc viết về nhà trường<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thay mặt sinh viên đang học tập tại trường, sinh viên Nguyễn Thị Uyên Vy – Lớp AVK38 – Ngành Tiếng Anh đã phát biểu tri ân và tặng hoa quý Thầy/Cô giáo của trường.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lễ kỷ niệm 34 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2016) và trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn diễn ra trong không khí trang trọng và thành công tốt đẹp.</p>\n<!-- /wp:paragraph -->', 'Trường Đại học Đà Lạt long trọng tổ chức kỷ niệm ngày Nhà giáo Việt Nam và trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn', '', 'publish', 'open', 'open', '', 'truong-dai-hoc-da-lat-long-trong-to-chuc-ky-niem-ngay-nha-giao-viet-nam-va-trao-bang-tien-si-danh-du-cho-ong-johnathan-hanh-nguyen', '', '', '2021-09-24 04:09:03', '2021-09-24 04:09:03', '', 0, 'http://localhost/wordpress/?p=235', 0, 'post', '', 0),
(236, 1, '2021-09-24 04:08:56', '2021-09-24 04:08:56', '', '051A9247', '', 'inherit', 'open', 'closed', '', '051a9247', '', '', '2021-09-24 04:08:56', '2021-09-24 04:08:56', '', 235, 'http://localhost/wordpress/wp-content/uploads/2021/09/051A9247.jpg', 0, 'attachment', 'image/jpeg', 0),
(237, 1, '2021-09-24 04:09:03', '2021-09-24 04:09:03', '<!-- wp:paragraph -->\n<p>&nbsp;Sáng ngày 20/11/2016, tại Hội trường Thư viện, Trường Đại học Đà Lạt đã long trọng tổ chức Lễ kỷ niệm 34 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2016) và trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tham dự buổi Lễ, Trường Đại học Đà Lạt rất vinh dự được đón tiếp:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em><strong>• Về khách mời:</strong></em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Trương Mỹ Hoa, nguyên Bí thư Trung ương Đảng, nguyên Phó Chủ tịch nước Cộng hòa xã hội chủ nghĩa Việt Nam.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Nguyễn Thị Minh, nguyên ủy viên Ban Chấp hành Trung ương Đảng, nguyên Bí thư Tỉnh ủy Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Lê Thị Bân, nguyên ủy viên Ban Chấp hành Trung ương Đảng, nguyên Bí thư Tỉnh ủy Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đồng chí Huỳnh Đức Hòa, nguyên Bí thư Tỉnh ủy, nguyên Chủ tịch Hội đồng nhân dân tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Nguyễn Thị Thu Thủy, nguyên Chủ tịch UBND tỉnh Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Trung tướng Triệu Xuân Hòa, nguyên Phó Trưởng ban chỉ đạo Tây Nguyên.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Nguyễn Thị Hiếu, Bí thư Huyện ủy Tân Châu, tỉnh Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Thiếu tướng Hoàng Văn Minh, Giám đốc Học viện lục quân Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Võ Thị Hảo, Giám đốc Sở khoa học và Công nghệ Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Đại tá Nguyễn Quang Thống, Phó Giám đốc Công an tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ông Phan Minh Thành, nguyên Trưởng ban khu kinh tế Tây Ninh.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ông Yoo Tae Huyn, Cựu Đại sứ Đại Hàn Dân Quốc tại Việt Nam; Giáo sư Danh dự Trường Đại học Đà Lạt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – TS. Đặng Quốc Chính, Chủ tịch HĐQT Công ty Cổ phần Du lịch Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;– Ông Johnathan Hạnh Nguyễn, Chủ tịch Hội đồng quản trị Công ty TNHH Xuất Nhập khẩu Liên Thái Bình Dương, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Bà Lê Hồng Thủy Tiên, Tổng Giám đốc Công ty TNHH Xuất Nhập khẩu Liên Thái Bình Dương, phu nhân ông Johnathan Hạnh Nguyễn.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em><strong>• Về phía nhà trường</strong></em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – PGS.TS. Nguyễn Đức Hòa, Bí thư Đảng ủy, Hiệu trưởng Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Các ông bà trong Ban Chấp hành Đảng bộ; Ban Giám hiệu; Hội đồng trường; Ban Chấp hành Công đoàn; Đoàn Thanh niên; Hội Sinh viên; Hội Cựu Chiến binh; các đồng chí lãnh đạo, nguyên lãnh đạo các phòng chức năng, khoa chuyên môn, viện nghiên cứu và các đơn vị trực thuộc; các cán bộ viên chức và 250 sinh viên, học viên cao học, nghiên cứu sinh đại diện cho hơn 13.000 sinh viên nhà trường.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A2471.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>PGS. TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng đọc diễn văn kỷ niệm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Về dự lễ hội truyền thống còn có các vị đại biểu đại diện lãnh đạo các sở, ban, ngành của tỉnh Lâm Đồng và thành phố Đà Lạt; các cơ quan Trung ương trên địa bàn tỉnh Lâm Đồng và các tỉnh trong khu vực Tây Nguyên, Nam Trung bộ và Đông Nam bộ; các tổ chức quốc tế, các giáo sư, tiến sĩ trong nước và nước ngoài đã tham gia giảng dạy, hợp tác với Trường Đại học Đà Lạt; các cơ quan truyền thông của Trung ương, thành phố Hồ Chí Minh và tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A2497.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>PGS. TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng&nbsp;trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn, cựu sinh viên ngành Quản trị kinh doanh, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt, Chủ tịch Hội đồng quản trị Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9091.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Đồng chí Trương Mỹ Hoa – Nguyên Bí thư Trung ương Đảng, nguyên Phó Chủ tịch nước Cộng hòa xã hội chủ nghĩa Việt Nam tặng hoa và phát biểu chúc mừng&nbsp;ông Johnathan Hạnh Nguyễn</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9169.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Ông Johnathan Hạnh Nguyễn – Tiến sĩ danh dự,&nbsp;cựu sinh viên ngành Quản trị kinh doanh, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt, Chủ tịch Hội đồng quản trị Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC phát biểu tại buổi lễ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong niềm vui phấn khởi, PGS.TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng thay mặt lãnh đạo nhà trường đã đọc diễn văn kỷ niệm 34 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2016) ôn lại truyền thống “Tôn sư trọng đạo” của dân tộc ta, ghi nhận những đóng góp to lớn của cán bộ và sinh viên các thế hệ nhà trường. Trong ngày lễ kỷ niệm long trọng hôm nay, Trường Đại học Đà Lạt quyết định trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn, cựu sinh viên ngành Quản trị kinh doanh, Ủy viên Hội đồng trường – Trường Đại học Đà Lạt, Chủ tịch Hội đồng quản trị Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC đã có những cống hiến, đóng góp to lớn cho đất nước và ngành giáo dục nói chung, Trường Đại học Đà Lạt nói riêng. Ông Johnathan Hạnh Nguyễn đã được Nhà nước tặng thưởng Huân chương Lao Động hạng Ba năm 2009, hạng Nhì năm 2011, hạng Nhất năm 2015; Huân chương Hữu nghị về những đóng góp tích cực trong xây dựng củng cố và phát triển mối quan hệ hữu nghị hợp tác giữa Việt Nam và Phillipines, cùng nhiều phần thưởng cao quý khác. Ông đã tích cực đóng góp cho các hoạt động xã hội từ thiện hướng về cộng đồng. Đặc biệt là hỗ trợ phát triển ngành giáo dục như: Tài trợ xây dựng trường học tại huyện đảo Trường Sa; đóng góp hơn 6 triệu USD vào các quỹ từ thiện xã hội; trao tặng hơn 40.000 suất học bổng cho trẻ em nghèo của 63 tỉnh thành không có điều kiện đến trường; cung cấp thiết bị phục vụ dạy và học tại các tỉnh vùng sâu, vùng biên giới, hải đảo; hiện nay Ông còn là Phó chủ nhiệm câu lạc bộ “Vì Hoàng Sa, Trường Sa thân yêu”.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Xúc động, vinh dự được nhận bằng Tiến sĩ danh dự đầu tiên của Trường Đại học Đà Lạt, ông Johnathan Hạnh Nguyễn gửi lời cảm ơn tới Đảng ủy, Ban Giám hiệu, Hội đồng trường, toàn thể cán bộ và sinh viên nhà trường. Ông cũng mong muốn sẽ tiếp tục đồng hành cùng tập thể lãnh đạo và cán bộ nhà trường để hoạch định chiến lược xây dựng Trường Đại học Đà Lạt không ngừng phát triển, thành trường đại học trọng điểm quốc gia và là điểm sáng của giáo dục nước nhà nói chung và Tây Nguyên nói riêng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ghi nhận những cống hiến của cán bộ và sinh viên trường Đại học Đà Lạt, trong năm học 2015 – 2016, Đảng, Nhà nước, Bộ Giáo dục và Đào tạo đã tặng nhiều phần thưởng cao quý cho tập thể và cá nhân của trường: 02 nhà giáo được Hội đồng Chức danh giáo sư Nhà nước công nhận đạt chuẩn chức danh Phó Giáo sư; 10 nhà giáo được Bộ Nội vụ bổ nhiệm chức danh giảng viên cao cấp; nhà trường được Bộ Giáo dục và Đào tạo tặng thưởng Cờ thi đua về thành tích xuất sắc trong công tác đào tạo nguồn nhân lực cho Tây Nguyên, duyên hải Nam Trung bộ và Đông Nam bộ; 08 đơn vị được công nhận là tập thể lao động xuất sắc; 44 cán bộ viên chức được tặng Bằng khen của Bộ GD&amp;ĐT; 10 cán bộ được tặng Kỷ niệm chương “Vì sự nghiệp giáo dục” trong đó có 05 cán bộ thuộc Công an các cấp tỉnh Lâm Đồng và 01 cán bộ lãnh đạo Sở Khoa học và Công nghệ Lâm Đồng đã có những đóng góp quan trọng trong công tác giữ vững an ninh chính trị, trật tự an toàn xã hội và nghiên cứu khoa học, hợp tác quốc tế, góp phần vào sự nghiệp xây dựng, phát triển nhà trường. Đặc biệt, Trường Đại học Đà Lạt đã được Chính phủ khẳng định đầu tư xây dựng thành trường đại học trọng điểm quốc gia vùng Tây Nguyên.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9205.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;Hiệu trưởng Nguyễn Đức Hòa gửi thư và hoa chúc mừng 06 cán bộ giảng viên của trường bảo vệ thành công luận án Tiến sĩ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9215.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Hiệu trưởng Nguyễn Đức Hòa và ông Huỳnh Đức Hòa trao Kỷ niệm chương&nbsp;“Vì sự nghiệp giáo dục” của Bộ trưởng Bộ Giáo dục cho các cán bộ trong và ngoài ngành Giáo dục</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9239.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Chủ tịch Hội đồng trường Cao Thế Trình trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo&nbsp;cho 06 cán bộ của Trường&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9247.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Lê Hồng Phong&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho 03 nguyên cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9259.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Nguyễn Văn Kết&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho các cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9264.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Nguyễn Duy Mậu&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho các cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9270.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Phó Hiệu trưởng Lê Minh Chiến&nbsp;trao bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo cho các cán bộ của Trường</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong buổi lễ long trọng này, Hiệu trưởng Nguyễn Đức Hòa gửi thư và hoa chúc mừng 06 cán bộ giảng viên của trường vừa bảo vệ thành công luận án Tiến sĩ, góp phần nâng cao uy tín và vị thế của nhà trường. Nhân dịp này, Trường Đại học Đà Lạt tổ chức trao phần thưởng âm nhạc cho 02 tác giả có sáng tác âm nhạc viết về nhà trường được cán bộ và sinh viên yêu thích:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ca khúc: Bài ca Đại học Đà Lạt – Tác giả: Nguyễn An Ninh, nguyên Hiệu trưởng Trường Đại học Đà Lạt giai đoạn 1990 – 1997.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; – Ca khúc: Giảng đường Tháp sao – Tác giả: Nguyễn Duy Chính, nguyên Giảng viên chính khoa Sinh học, Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đại diện cán bộ được nhận Kỷ niệm chương “Vì sự nghiệp giáo dục”, bà Võ Thị Hảo, Giám đốc Sở khoa học &amp; Công nghệ Lâm Đồng, cựu sinh viên nhà trường đã phát biểu tri ân và mong muốn tiếp tục đóng góp một phần nhỏ bé vào sự nghiệp giáo dục – đào tạo nói chung, nghiên cứu khoa học và chuyển giao công nghệ của trường nói riêng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://tchc.dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2016/Th%C3%A1ng%2011/L%E1%BB%85%2020-11/051A9278.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Hiệu trưởng Nguyễn Đức Hòa&nbsp;trao phần thưởng âm nhạc cho tác giả có sáng tác âm nhạc viết về nhà trường<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thay mặt sinh viên đang học tập tại trường, sinh viên Nguyễn Thị Uyên Vy – Lớp AVK38 – Ngành Tiếng Anh đã phát biểu tri ân và tặng hoa quý Thầy/Cô giáo của trường.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lễ kỷ niệm 34 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2016) và trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn diễn ra trong không khí trang trọng và thành công tốt đẹp.</p>\n<!-- /wp:paragraph -->', 'Trường Đại học Đà Lạt long trọng tổ chức kỷ niệm ngày Nhà giáo Việt Nam và trao bằng Tiến sĩ danh dự cho ông Johnathan Hạnh Nguyễn', '', 'inherit', 'closed', 'closed', '', '235-revision-v1', '', '', '2021-09-24 04:09:03', '2021-09-24 04:09:03', '', 235, 'http://localhost/wordpress/?p=237', 0, 'revision', '', 0),
(238, 1, '2021-09-24 04:10:00', '2021-09-24 04:10:00', '<!-- wp:paragraph -->\n<p>Nhân kỷ niệm 71 năm ngày thương binh – liệt sỹ (27/7/1947 – 27/7/2018), vào lúc 9 giờ 00 ngày 27/7/2018, Trường Đại học Đà Lạt đã long trọng tổ chức chương trình kỷ niệm 71 năm ngày thương binh liệt sỹ.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tham dự chương trình có các đồng chí trong Đảng ủy, Ban Giám hiệu, lãnh đạo Hội Cựu chiến binh, lãnh đạo Đoàn TNCSHCM và Hội sinh viên Trường Đại học Đà Lạt, đại diện lãnh đạo các phòng chức năng trong trường. Đặc biệt là sự có mặt của thân nhân các gia đình liệt sỹ, thương binh là cán bộ, viên chức của Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tại buổi lễ, các đồng chí lãnh đạo nhà trường đã ôn lại lịch sử hào hùng, truyền thống bất khuất, anh dũng của dân tộc, sự hy sinh xương máu của các anh hùng liệt sỹ, thương binh, bệnh binh vì dân tộc, vì công cuộc xây dựng và bảo vệ Tổ quốc.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Đây cũng là dịp để cán bộ nhà trường thể hiện trách nhiệm, tình cảm của mình để tri ân đến công lao của các anh hùng, liệt sỹ, thương binh đã không tiếc máu xương, chiến đấu anh dũng hy sinh vì độc lập tự do của dân tộc để giải phóng và thống nhất đất nước; khơi dậy truyền thống yêu nước và đạo lý truyền thống uống nước nhớ nguồn cho các thế hệ con cháu; nâng cao tinh thần đoàn kết, phấn đấu trong công tác, học tập, cống hiến, ra sức xây dựng trường Đại học Đà Lạt nói riêng và quê hương Lâm Đồng nói chung ngày càng phát triển, đặc biệt là các thế hệ trẻ trong công cuộc xây dựng và bảo vệ Tổ quốc hiện nay.</p>\n<!-- /wp:paragraph -->', 'Trường Đại học Đà Lạt tổ chức chương trình kỷ niệm 71 năm ngày thương binh liệt sỹ (27/7/1947 – 27/7/2018)', '', 'publish', 'open', 'open', '', 'truong-dai-hoc-da-lat-to-chuc-chuong-trinh-ky-niem-71-nam-ngay-thuong-binh-liet-sy-27-7-1947-27-7-2018', '', '', '2021-09-24 04:10:00', '2021-09-24 04:10:00', '', 0, 'http://localhost/wordpress/?p=238', 0, 'post', '', 0),
(239, 1, '2021-09-24 04:09:55', '2021-09-24 04:09:55', '', '1', '', 'inherit', 'open', 'closed', '', '1', '', '', '2021-09-24 04:09:55', '2021-09-24 04:09:55', '', 238, 'http://localhost/wordpress/wp-content/uploads/2021/09/1.jpg', 0, 'attachment', 'image/jpeg', 0),
(240, 1, '2021-09-24 04:10:00', '2021-09-24 04:10:00', '<!-- wp:paragraph -->\n<p>Nhân kỷ niệm 71 năm ngày thương binh – liệt sỹ (27/7/1947 – 27/7/2018), vào lúc 9 giờ 00 ngày 27/7/2018, Trường Đại học Đà Lạt đã long trọng tổ chức chương trình kỷ niệm 71 năm ngày thương binh liệt sỹ.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tham dự chương trình có các đồng chí trong Đảng ủy, Ban Giám hiệu, lãnh đạo Hội Cựu chiến binh, lãnh đạo Đoàn TNCSHCM và Hội sinh viên Trường Đại học Đà Lạt, đại diện lãnh đạo các phòng chức năng trong trường. Đặc biệt là sự có mặt của thân nhân các gia đình liệt sỹ, thương binh là cán bộ, viên chức của Trường Đại học Đà Lạt.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tại buổi lễ, các đồng chí lãnh đạo nhà trường đã ôn lại lịch sử hào hùng, truyền thống bất khuất, anh dũng của dân tộc, sự hy sinh xương máu của các anh hùng liệt sỹ, thương binh, bệnh binh vì dân tộc, vì công cuộc xây dựng và bảo vệ Tổ quốc.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Đây cũng là dịp để cán bộ nhà trường thể hiện trách nhiệm, tình cảm của mình để tri ân đến công lao của các anh hùng, liệt sỹ, thương binh đã không tiếc máu xương, chiến đấu anh dũng hy sinh vì độc lập tự do của dân tộc để giải phóng và thống nhất đất nước; khơi dậy truyền thống yêu nước và đạo lý truyền thống uống nước nhớ nguồn cho các thế hệ con cháu; nâng cao tinh thần đoàn kết, phấn đấu trong công tác, học tập, cống hiến, ra sức xây dựng trường Đại học Đà Lạt nói riêng và quê hương Lâm Đồng nói chung ngày càng phát triển, đặc biệt là các thế hệ trẻ trong công cuộc xây dựng và bảo vệ Tổ quốc hiện nay.</p>\n<!-- /wp:paragraph -->', 'Trường Đại học Đà Lạt tổ chức chương trình kỷ niệm 71 năm ngày thương binh liệt sỹ (27/7/1947 – 27/7/2018)', '', 'inherit', 'closed', 'closed', '', '238-revision-v1', '', '', '2021-09-24 04:10:00', '2021-09-24 04:10:00', '', 238, 'http://localhost/wordpress/?p=240', 0, 'revision', '', 0),
(241, 1, '2021-09-24 04:10:50', '2021-09-24 04:10:50', '<!-- wp:paragraph -->\n<p>Hòa chung không khí của cả nước chào mừng ngày Nhà giáo Việt Nam, chiều ngày 20 tháng 11 tại Hội trường Thư viện, Trường Đại học Đà Lạt long trọng tổ chức Lễ kỷ niệm 33 năm ngày nhà giáo Việt Nam (20/11/1982 – 20/11/2015).</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tham dự buổi Lễ, Nhà trường rất vinh dự được đón tiếp:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>–&nbsp;Đồng chí Trần Việt Hùng, Phó Trưởng ban Thường trực, Ban Chỉ đạo Tây Nguyên, Ủy viên Hội đồng trường Trường Đại học Đà Lạt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>–&nbsp;Đồng chí Trần Đức Quận, Phó Bí thư Tỉnh ủy Lâm Đồng;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>– Ông Bùi Hồng Quang, Phó Vụ trưởng Vụ Kế hoạch – Tài chính, Bộ Giáo dục và Đào tạo, Ủy viên Hội đồng trường Trường Đại học Đà Lạt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>–&nbsp;Ông Yoo Tae Huyn, Cựu Đại sứ Đại Hàn Dân Quốc tại Việt Nam; Giáo sư Danh dự Trường Đại học Đà Lạt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>–&nbsp;Ông Johnathan Hạnh Nguyễn – Chủ tịch HĐQT Công ty TNHH Liên Thái Bình Dương IMEX PAN PACIFIC, Ủy viên Hội đồng trường Trường Đại học Đà Lạt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>–&nbsp;Các đồng chí lãnh đạo đại diện Tỉnh ủy Lâm Đồng; lãnh đạo các Sở, Ban ngành của tỉnh; các tổ chức quốc tế; các trường đại học, cao đẳng, viện nghiên cứu và các cơ sở liên kết đào tạo với trường; đại diện các cơ quan thông tấn báo chí Trung ương và tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Về phía nhà trường có PGS.TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng; TS. Lê Hồng Phong – Phó Bí thư Đảng ủy, Phó Hiệu trưởng; TS. Nguyễn Duy Mậu – Phó Hiệu trưởng; ThS. Lê Minh Chiến – Đảng ủy viên, Phó Hiệu trưởng; các đồng chí nguyên lãnh đạo của trường; các đồng chí trong Ban Chấp hành Đảng bộ, Ban Chấp hành Công đoàn, Đoàn TNCS Hồ Chí Minh, Hội Sinh viên, Hội Cựu Chiến binh cùng toàn thể các nhà khoa học, cán bộ viên chức và học viên, sinh viên đang học tập tại trường.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Trong ngày lễ long trọng này, Nhà trường đã nhận được nhiều điện hoa chúc mừng của các cơ quan Trung ương, tỉnh Lâm Đồng, thành phố Đà Lạt, các tổ chức quốc tế, cán bộ và cựu sinh viên của trường.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1124.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1126.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1128.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Mở đầu chương trình, PGS.TS. Nguyễn Đức Hòa – Bí thư Đảng ủy, Hiệu trưởng đọc diễn văn chào mừng ngày Nhà giáo Việt Nam; đồng chí Hiệu trưởng đã thay mặt Đảng ủy, Ban Giám hiệu, cán bộ viên chức và sinh viên nhà trường trân trọng gửi lời cảm ơn tới các đồng chí lãnh đạo Bộ Giáo dục và Đào tạo, Ban Chỉ đạo Tây Nguyên, các Bộ, Ban ngành của Trung ương; lãnh đạo tỉnh Lâm Đồng và thành phố Đà Lạt đã quan tâm, giúp đỡ Trường Đại học Đà Lạt trong thời gian qua; gửi lời cảm ơn chân thành, lời tri ân và lời chúc mừng tốt đẹp nhất tới các thế hệ nhà giáo, cán bộ viên chức của trường.&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1118.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1119.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Thay mặt Tỉnh ủy, UBND tỉnh Lâm Đồng, đồng chí Trần Đức Quận – Phó Bí thư Tỉnh ủy phát biểu chúc mừng và ghi nhận những đóng góp của tập thể lãnh đạo, cán bộ và sinh viên nhà trường trong công tác đào tạo nguồn nhân lực trình độ cao cho Tây Nguyên nói chung, tỉnh Lâm Đồng nói riêng và các hoạt động phong trào của Tỉnh nhà, góp phần vào thành công chung của tỉnh Lâm Đồng trong công tác giữ vững ổn định an ninh chính trị, phục vụ tốt công tác phát triển kinh tế – xã hội của địa phương. Đồng chí Trần Đức Quận cũng đề nghị tập thể Thầy và trò trường Đại học Đà Lạt tiếp tục đoàn kết, phát huy những thành tích đã đạt được, đẩy mạnh việc học tập và làm theo tấm gương đạo đức Hồ Chí Minh, thi đua “dạy tốt – học tốt” lập nhiều thành tích chào mừng thành công Đại hội Đảng các cấp, xứng đáng là một bộ phận không tách rời của tỉnh Lâm Đồng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1135.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1138.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Nhân dịp này, Bộ trưởng Bộ Giáo dục và Đào tạo đã ký quyết định tặng Kỷ niệm chương “Vì sự nghiệp giáo dục” cho 09 cán bộ viên chức của trường đã có công đóng góp cho sự nghiệp giáo dục – đào tạo của đất nước; tặng Bằng khen cho 10 cán bộ đã có nhiều đóng góp xây dựng đơn vị, góp phần vào sự nghiệp phát triển giáo dục – đào tạo và Quyết định công nhận danh hiệu thi đua năm học 2014 – 2015 cho 08 tập thể lao động xuất sắc của trường. Công đoàn ngành giáo dục Việt Nam tặng Bằng khen cho 03 cá nhân và 01 tập thể có thành tích xuất sắc trong công tác năm học 2014 – 2015.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1143.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1145.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1148.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1151.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1153.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Tại buổi Lễ, Hiệu trưởng Nguyễn Đức Hòa cũng chúc mừng 12 tân Tiến sĩ của trường đã hoàn thành xuất sắc chương trình Nghiên cứu sinh và trở về trường công tác, góp phần nâng cao uy tín và vị thế của nhà trường. Hiệu trưởng đề nghị các tân Tiến sĩ tiếp tục nghiên cứu khoa học để đạt được nhiều thành tích cao hơn nữa, thực hiện tốt công tác đào tạo nguồn nhân lực chất lượng cao, phục vụ sự nghiệp công nghiệp hóa, hiện đại hóa đất nước nói chung và sự phát triển của Trường Đại học Đà Lạt nói riêng.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Trong ngày lễ trọng đại này, Tiến sĩ Nguyễn Văn Tuấn – Giảng viên khoa Kinh tế – QTKD đại diện cho cán bộ giảng viên và sinh viên Nguyễn Phan Hoài Hương – Lớp Du lịch K37, Khoa Quản trị Du lịch, đại diện cho sinh viên đang học tập tại trường phát biểu tri ân quý Thầy Cô. Trong bài phát biểu, sinh viên Nguyễn Phan Hoài Hương bày tỏ:&nbsp;<em>“Trong tiết trời se lạnh của tháng 11 đã về. Không khí của những ngày đầu đông, không khí của mùa yêu thương, của niềm tự hào, của sự tri ân và những mong ước, kỷ niệm trở về của những tháng ngày cắp sách tới trường. Tháng 11, tháng mà có lẽ quý thầy cô giáo sẽ cảm thấy hạnh phúc nhất vì những công lao, tâm huyết, thành quả của mình đã được cả xã hội tôn vinh như Nhà văn John Steinbeck đã viết: “Dạy học là một nghệ thuật vĩ đại nhất vì nó kết hợp giữa lý trí và tinh thần”, và ngày hôm nay, chúng em có mặt ở đây xin được phép tôn vinh quý Thầy Cô – những người đã, đang và sẽ theo đuổi nghệ thuật vĩ đại ấy. Xin gửi đến quý Thầy Cô lời cảm ơn và biết ơn sâu sắc vì sự hiện diện của quý Thầy Cô đã giúp chúng em trưởng thành hơn không chỉ về kiến thức mà còn ở niềm tin, kỹ năng và thái độ. Thầy cô là người truyền thụ cho chúng em những tri thức, tư duy khoa học và sống luôn có hoài bão. Những tri thức đó không chỉ có trên các trang sách mà là cái nồng nàn với cuộc sống, cái hơi thở của khó khăn đời thường, cái cố gắng vươn lên làm giàu phục vụ cho quê hương, đất nước, bản thân và gia đình”.</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1157.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://dlu.edu.vn/Resources/Images/SubDomain/HomePage/TinTuc/2015/Thang11/L%E1%BB%85%2020-11/DSC_1159.jpg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Lễ kỷ niệm ngày Nhà giáo Việt Nam diễn ra trong không khí trang trọng và thành công tốt đẹp. Kính chúc quý Thầy cô giáo và gia đình luôn luôn mạnh khỏe, hạnh phúc, cống hiến ngày càng nhiều cho sự nghiệp phát triển giáo dục của đất nước nói chung và của trường Đại học Đà Lạt nói riêng.</p>\n<!-- /wp:paragraph -->', 'Trường Đại học Đà Lạt long trọng tổ chức Lễ kỷ niệm 33 năm ngày Nhà giáo Việt Nam (20/11/1982 – 20/11/2015)', '', 'publish', 'open', 'open', '', 'truong-dai-hoc-da-lat-long-trong-to-chuc-le-ky-niem-33-nam-ngay-nha-giao-viet-nam-20-11-1982-20-11-2015', '', '', '2021-09-24 04:10:50', '2021-09-24 04:10:50', '', 0, 'http://localhost/wordpress/?p=241', 0, 'post', '', 0),
