(281, 1, '2021-09-27 07:08:27', '2021-09-27 07:08:27', '<!-- wp:paragraph -->\n<p>Thông tin sinh viên:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tên: Nguyễn Khánh Linh</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MSSV: 1812790</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lớp: CTK42</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Bài thực hành lab02 môn ptudmnm</p>\n<!-- /wp:paragraph -->', 'Liên hệ', '', 'publish', 'closed', 'closed', '', 'lien-he', '', '', '2021-09-27 07:08:27', '2021-09-27 07:08:27', '', 0, 'http://localhost/wordpress/?page_id=281', 0, 'page', '', 0),
